  import React from 'react'
  import { MDXTag } from '@mdx-js/tag'
  import Code from '../components/code'

export default ({components, ...props}) => <MDXTag name="wrapper"  components={components}>
<MDXTag name="h1" components={components} props={{"id":"styles"}}>{`Styles`}</MDXTag>
<MDXTag name="h2" components={components} props={{"id":"add-styles"}}>{`Add Styles`}</MDXTag>
<MDXTag name="p" components={components}>{`In order to have an app that looks on point we will need to provide some base styles that you are of course more then welcome to edit.`}</MDXTag>
<MDXTag name="p" components={components}>{`In our `}<MDXTag name="inlineCode" components={components} parentName="p">{`index.js`}</MDXTag>{` we had this line:`}</MDXTag>
<Code code={`import './styles.css'`} />
<MDXTag name="p" components={components}>{`This means that this App will import this CSS file and in there we can place all the styles needed to make our app amazing.`}</MDXTag>
<MDXTag name="p" components={components}>{`You can copy these styles:`}</MDXTag>
<Code code={`
main {
  font-family: sans-serif;
  text-align: center;
  width: 100%;
  min-height: 100%;
  background-repeat: repeat;
  background-image: url("https://66.media.tumblr.com/b58f700c402dee1975464dc284ae91f0/tumblr_oslh4vbHPl1ws4s25o1_500.png");
  padding: 1em 0;
}
\n
body * {
  box-sizing: border-box;
}
\n
body, html, #root {
  height: 100%;
  margin: 0;
  padding: 0;
}
\n
h1 {
  font-size: 40px;
  font-weight: normal;
  margin-top: 10px;
}
\n
figure {
  margin: 0;
  padding: 0;
  max-width: 100%;
}
\n
@keyframes fadeIn {
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
}
\n
.cat-placeholder {
  max-width: 100%;
}
\n
.cat-image {
  width: auto;
  max-width: 100%;
  height: 100%;
  display: block;
  margin: auto;
  animation: fadeIn 200ms ease-in;
}
\n
.cat-wrapper {
  width: 80%;
  margin: auto;
  padding: 10px;
  background: #fff;
  border-radius: 4px;
}
\n
.cat-container {
  box-shadow: 1px 1px 5px lightgray;
  margin-bottom: 40px;
}
\n
.cat-actions {
  padding-bottom: 10px;
  padding-right: 10px;
  width: 100%;
  display: flex;
  list-style: none;
  justify-content: flex-end;
}
\n
button {
  background: none;
  border: none;
  width: 40px;
  height: 40px;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 0;
}
\n
button:hover {
  background-color: lightgrey;
  border-radius: 50%;
}
\n
.favorite-cats {
  display: flex;
  list-style: none;
  flex-wrap: wrap;
}
\n
.heart-icon svg {
  fill: none;
  stroke: #f44336;
  stroke-width: 1px;
  stroke-linejoin: round;
}
\n
.heart-icon:disabled svg {
  fill: #f44336;
  opacity: 0.25;
}
\n
.favorite-cats li {
  display: block;
  width: 25%;
  margin: 5px;
  box-shadow: 0px 0px 2px lightgray;
}
\n
.favorite-cats li button {
  float: right;
}
\n
.favorite-cat {
  width: 100%;
}
`} language="css"/>
